mapper.insert:
select nvl(max(chat_id), 1) + 1 chatId from kakao_chats where room_id = #{roomId}
INSERT INTO kakao_chats (chat_id, email, room_id, status, type, content, createAt) values ( #{chatId, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}, #{roomId, jdbcType=VARCHAR}, 1, #{chatType, jdbcType=VARCHAR}, #{content, jdbcType=VARCHAR},
to_date(#{createAt}, 'YYYY-MM-DD HH24:MI:SS')
sysdate
)

mapper.delete:
update KAKAO_CHATS
set STATUS = 2
where CHAT_ID = #{chatId}
  and ROOM_ID = #{roomId}
  and EMAIL = #{email}

mapper.update:
update KAKAO_READ_USERS
set CHAT_ID = (select max(KAKAO_CHATS.CHAT_ID) from KAKAO_CHATS where KAKAO_CHATS.ROOM_ID = #{roomId})
where ROOM_ID = #{roomId}
  and EMAIL = #{email}
